INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'english',
              'English 2nd Paper : Changing Sentence',
              'Transform the sentence into an exclamatory sentence: ''It is a very beautiful place.''',
              ARRAY['It is a very beautiful place!', 'What a beautiful place it is!', 'How a beautiful place it is!', 'The place is very beautiful.'],
              1,
              'Exclamatory sentences (আবেগসূচক বাক্য) starting with ''What a/What an'' are used for singular nouns. The correct structure is ''What a + adjective + noun + subject + verb!'' Here, ''place'' is a singular noun, so ''What a beautiful place it is!'' is correct.'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'english',
              'English 2nd Paper : Changing Sentence',
              'Which one is the correct passive voice of ''Do you know him?''',
              ARRAY['Is he known to you?', 'Is he known by you?', 'Was he known to you?', 'Does he known to you?'],
              0,
              'In passive voice (কর্মবাচ্য), the object becomes the subject. The active sentence is in the present indefinite tense. The passive form is ''Is/Am/Are + subject + past participle + by/to?'' For the verb ''know'', we use ''to'' instead of ''by'' after the past participle.'
            );
