INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'ict',
              'ICT অধ্যায় ৫',
              'নিচের প্রোগ্রামটির আউটপুট কী?<br>#include &lt;stdio.h&gt;<br>int main() {<br>    int i, j, n=5;<br>    for(i=1; i&lt;=n; i++) {<br>        for(j=1; j&lt;=n-i; j++)<br>            printf(" ");<br>        for(j=1; j&lt;=i; j++)<br>            printf("*");<br>        printf("\n");<br>    }<br>    return 0;<br>}<br>উদ্দীপকের আউটপুট কেমন হবে?',
              ARRAY['সমকোণী ত্রিভুজ', 'সমবাহু ত্রিভুজ', 'উল্টো ত্রিভুজ', 'বর্গ'],
              0,
              'একটি সমকোণী ত্রিভুজ? না, এটি ডান দিকে সাজানো সমকোণী ত্রিভুজ (right-angled triangle)। প্রথমে স্পেস প্রিন্ট করে * প্রিন্ট করায় এটি ডান দিকে সারিবদ্ধ হয়।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
