              ARRAY['printf এ ফরম্যাট ভুল', 'main ফাংশনের শেষে } নেই', 'x ডিক্লেয়ার করা হয়নি', 'কোন ত্রুটি নেই'],
              1,
              'main ফাংশনের শেষ কার্লি ব্র্যাকেট } নেই, যা সিনট্যাক্স ত্রুটি।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'ict',
              'ICT অধ্যায় ৫',
              'উদ্দীপক:<br>#include &lt;stdio.h&gt;<br>int main() {<br>    int a=5, b=5, c=10;<br>    if(a==b && a==c)<br>        printf("All Equal");<br>    else if(a==b || a==c || b==c)<br>        printf("Two Equal");<br>    else<br>        printf("All Different");<br>    return 0;<br>}<br>উদ্দীপকের আউটপুট কী?',
              ARRAY['All Equal', 'Two Equal', 'All Different', 'কিছুই না'],
              1,
              'a==b সত্য, a==c মিথ্যা, b==c মিথ্যা, কিন্তু OR অপারেটরের কারণে a==b সত্য হওয়ায় দ্বিতীয় শর্ত সত্য, ''Two Equal'' প্রিন্ট হয়।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'ict',
              'ICT অধ্যায় ৫',
              'C প্রোগ্রামিং ভাষায় ''&'' অপারেটর কী হিসেবে ব্যবহৃত হয়?',
              ARRAY['লজিক্যাল AND', 'বিটওয়াইজ AND', 'অ্যাড্রেস অব অপারেটর', 'অ্যাসাইনমেন্ট অপারেটর'],
              2,
              'C তে ''&'' অপারেটর মূলত ''অ্যাড্রেস অব'' অপারেটর হিসেবে ব্যবহৃত হয়, যা একটি ভেরিয়েবলের মেমরি অ্যাড্রেস রিটার্ন করে। এটি scanf-এ ভেরিয়েবলের অ্যাড্রেস পাস করতেও ব্যবহৃত হয়। বিটওয়াইজ AND-এর জন্যও ''&'' ব্যবহার করা হয়, কিন্তু সাধারণ প্রসঙ্গে (scanf) এটি অ্যাড্রেস অপারেটর হিসেবে পরিচিত।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'ict',
              'ICT অধ্যায় ৫',
              'নিচের কোনটি C তে ভ্যালিড (বৈধ) কন্সট্যান্ট?',
              ARRAY['3.14e-2', '0x1A', '''\n''', 'উপরের সবগুলো'],
              3,
              '3.14e-2 বৈজ্ঞানিক নোটেশনে float, 0x1A হেক্সাডেসিমেল ইন্টিজার, ''\n'' escape sequence ক্যারেক্টার—সবগুলোই C তে বৈধ কন্সট্যান্ট।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'ict',
              'ICT অধ্যায় ৫',
              'উদ্দীপক:<br>#include &lt;stdio.h&gt;<br>int main() {<br>    int a[5] = {10,20,30,40,50};<br>    int *p = a + 3;<br>    printf("%d", *p);<br>    return 0;<br>}<br>উদ্দীপকের আউটপুট কী?',
              ARRAY['30', '40', '50', 'কম্পাইল এরর'],
              1,
              'a অ্যারের নাম প্রথম এলিমেন্টের অ্যাড্রেস নির্দেশ করে। a+3 মানে অ্যারের তৃতীয় ইনডেক্স (a[3])-এর অ্যাড্রেস। *p = a[3] = 40।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'ict',
              'ICT অধ্যায় ৫',
              'নিচের ফাংশনটি কী রিটার্ন করবে?<br>int mystery(int n) {<br>    if(n==0) return 1;<br>    else return n * mystery(n-1);<br>}',
              ARRAY['n এর বর্গ', 'n এর ফ্যাক্টোরিয়াল', '2^n', 'n(n+1)/2'],
              1,
              'এটি একটি রিকার্সিভ ফাংশন যা n! (ফ্যাক্টোরিয়াল) নির্ণয় করে। base case n=0 হলে 1 রিটার্ন করে, যা 0! = 1।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
