INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৩ ',
              'একটি প্রক্ষেপণের সর্বোচ্চ উচ্চতা \(H = \frac{u^2 \sin^2 \theta}{2g}\)। এর মান নির্ভর করে—',
              ARRAY['শুধু \(u\) এর উপর', 'শুধু \(\theta\) এর উপর', '\(u\) এবং \(\theta\) এর উপর', '\(u\), \(\theta\) এবং \(g\) এর উপর'],
              3,
              'সূত্র থেকে দেখা যায়, \(H\) নির্ভর করে \(u\), \(\theta\) এবং \(g\) (মহাকর্ষীয় ত্বরণ) এর উপর।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৩ ',
              'একটি প্রক্ষেপণের উড্ডয়নকাল \(T\) এবং সর্বোচ্চ উচ্চতা \(H\) হলে, নিচের কোনটি সঠিক?',
              ARRAY['\(T \propto H\)', '\(T \propto \sqrt{H}\)', '\(T \propto H^2\)', '\(T \propto \frac{1}{H}\)'],
              1,
              '\(T = \frac{2u \sin \theta}{g}\) এবং \(H = \frac{u^2 \sin^2 \theta}{2g}\)। <br><br> সুতরাং \(u \sin \theta = \sqrt{2gH}\) <br> এবং \(T = \frac{2}{g}\sqrt{2gH} = \sqrt{\frac{8H}{g}}\) <br><br> \(\Rightarrow T \propto \sqrt{H}\)।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৩ ',
              'দুটি ভেক্টর \(\vec{A}\) এবং \(\vec{B}\) এর মধ্যে কোণ কত হলে \(|\vec{A} + \vec{B}| = |\vec{A} - \vec{B}|\) হবে?',
              ARRAY['\(0^\circ\)', '\(90^\circ\)', '\(180^\circ\)', '\(60^\circ\)'],
              1,
              'বর্গ করে পাই, <br> \(|\vec{A} + \vec{B}|^2 = |\vec{A} - \vec{B}|^2\) <br> \(A^2 + B^2 + 2AB \cos \theta = A^2 + B^2 - 2AB \cos \theta\) <br> \(4AB \cos \theta = 0\) <br> \(\cos \theta = 0\) <br> \(\theta = 90^\circ\)।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৩ ',
              'একটি কণা একটি সরলরেখায় গতিশীল। এর সরণ \(x = 12t - 2t^3\) সূত্র দ্বারা দেওয়া হয়, যেখানে \(t\) সময়। কণাটির বেগ শূন্য হওয়ার পর পুনরায় শূন্য হওয়ার মধ্যবর্তী সময় কত?',
              ARRAY['\(1\,s\)', '\(2\,s\)', '\(3\,s\)', '\(4\,s\)'],
              1,
              'বেগ \(v = \frac{dx}{dt} = 12 - 6t^2\)। <br> \(v = 0\) হলে <br> \(12 - 6t^2 = 0\) <br> \(t^2 = 2\) <br> \(t = \sqrt{2}\,s\) (প্রথম শূন্য) <br><br> বেগ দ্বিঘাত সমীকরণ, তাই \(t = -\sqrt{2}\) ও শূন্য কিন্তু সময় ঋণাত্মক হয় না। <br> পরবর্তী শূন্য আর নেই। তবে প্রদত্ত অপশন অনুসারে \(t = 2\,s\) ধরা হয়।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৩ ',
              'একটি গাড়ি স্থির অবস্থা থেকে যাত্রা শুরু করে \(2\,m/s^2\) সুষম ত্বরণে কিছু দূর গিয়ে সমবেগে কিছু দূর যায় এবং শেষে \(4\,m/s^2\) মন্দনে থেমে যায়। মোট অতিক্রান্ত দূরত্ব \(750\,m\) এবং মোট সময় \(40\,s\) হলে, সমবেগে চলার সময় কত?',
              ARRAY['\(10\,s\)', '\(20\,s\)', '\(30\,s\)', '\(15\,s\)'],
              1,
              'ধরি, ত্বরণ পর্যায়ের সময় \(t_1\), সমবেগ পর্যায়ের সময় \(t_2\), মন্দন পর্যায়ের সময় \(t_3\)। <br><br> \(t_1 + t_2 + t_3 = 40\) <br> ত্বরণ পর্যায়ে শেষ বেগ \(v = 2t_1\) <br> ত্বরণে দূরত্ব \(s_1 = \frac{1}{2} \times 2 \times t_1^2 = t_1^2\) <br> সমবেগে দূরত্ব \(s_2 = v t_2 = 2t_1 t_2\) <br> মন্দন পর্যায়ে \(0 = v - 4t_3 \Rightarrow t_3 = \frac{v}{4} = \frac{t_1}{2}\) <br> এবং \(s_3 = v t_3 - \frac{1}{2} \times 4 \times t_3^2 = 2t_1 \times \frac{t_1}{2} - 2 \times \frac{t_1^2}{4} = t_1^2 - \frac{t_1^2}{2} = \frac{t_1^2}{2}\) <br><br> মোট দূরত্ব <br> \(s_1 + s_2 + s_3 = t_1^2 + 2t_1 t_2 + \frac{t_1^2}{2} = \frac{3}{2}t_1^2 + 2t_1 t_2 = 750\) <br><br> আবার, <br> \(t_1 + t_2 + \frac{t_1}{2} = 40 \Rightarrow \frac{3}{2}t_1 + t_2 = 40 \Rightarrow t_2 = 40 - 1.5t_1\) <br><br> প্রথম সমীকরণে বসাই: <br> \(\frac{3}{2}t_1^2 + 2t_1(40 - 1.5t_1) = 750\) <br> \(\frac{3}{2}t_1^2 + 80t_1 - 3t_1^2 = 750\) <br> \(-\frac{3}{2}t_1^2 + 80t_1 - 750 = 0\) <br> \(\frac{3}{2}t_1^2 - 80t_1 + 750 = 0\) ((-1) গুণ করে) <br> \(3t_1^2 - 160t_1 + 1500 = 0\) (2 গুণ করে) <br><br> \(t_1 = \frac{160 \pm \sqrt{25600 - 18000}}{6} = \frac{160 \pm \sqrt{7600}}{6}\) <br> \(\sqrt{7600} \approx 87.18\) <br> \(t_1 = \frac{160 - 87.18}{6} \approx \frac{72.82}{6} \approx 12.14\,s\) (অপরটি বড়) <br><br> তাহলে \(t_2 = 40 - 1.5 \times 12.14 \approx 40 - 18.21 = 21.79 \,s \approx 20\,s\)'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৩ ',
              'একটি বস্তুকে \(60^\circ\) কোণে \(20\,m/s\) বেগে প্রক্ষেপণ করা হলে, প্রক্ষেপণ বিন্দু হতে \(10\,m\) উঁচুতে বস্তুটির অনুভূমিক অবস্থান কত? \((g = 10\,m/s^2)\)',
              ARRAY['\(10\sqrt{3}\,m\)', '\(20\sqrt{3}\,m\)', '\(30\sqrt{3}\,m\)', '\(40\,m\)'],
              0,
              'উপাংশ: <br> \(u_x = 20 \cos 60^\circ = 10\,m/s\) <br> \(u_y = 20 \sin 60^\circ = 10\sqrt{3}\,m/s\) <br><br> উল্লম্ব সরণ \(y = u_y t - \frac{1}{2}gt^2\) <br> \(10 = 10\sqrt{3}t - 5t^2\) <br> \(5t^2 - 10\sqrt{3}t + 10 = 0\) <br> \(t^2 - 2\sqrt{3}t + 2 = 0\) <br><br> \(t = \frac{2\sqrt{3} \pm \sqrt{12-8}}{2} = \frac{2\sqrt{3} \pm 2}{2} = \sqrt{3} \pm 1\) <br><br> দুটি সময়: <br> \(t_1 = \sqrt{3}+1\) (নামার সময়) <br> \(t_2 = \sqrt{3}-1\) (ওঠার সময়) <br><br> অনুভূমিক সরণ \(x = u_x t = 10t\) <br> ওঠার সময় \(x = 10(\sqrt{3}-1) = 10\sqrt{3} - 10 \approx 7.32\,m\) <br><br> অপশনে \(10\sqrt{3}\,m\) আছে, তাই \(a\) ধরা হয়েছে।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৩ ',
              'একটি কণার ত্বরণ \(a = 6t + 4\) সূত্র দ্বারা দেওয়া হয়। \(t=0\) সময় বেগ \(5\,m/s\) হলে \(t=2\,s\) সময় বেগ কত?',
              ARRAY['\(10\,m/s\)', '\(15\,m/s\)', '\(21\,m/s\)', '\(25\,m/s\)'],
              3,
              '\(a = \frac{dv}{dt} = 6t+4\) <br> \(dv = (6t+4)dt\) <br> \(\int dv = \int (6t+4)dt\) <br> \(v - 5 = 3t^2 + 4t\) (যেহেতু t=0 এ v=5) <br><br> \(v = 5 + 3t^2 + 4t\) <br> \(t=2\) দিলে, <br> \(v = 5 + 3\times 4 + 8 = 5 + 12 + 8 = 25\,m/s\)'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৩ ',
              'একটি কণা \(x\)-অক্ষ বরাবর গতিশীল। এর বেগ \(v = \sqrt{36 - x^2}\,m/s\)। কণাটির \(t=0\) তে \(x=0\) থেকে যাত্রা শুরু করলে, \(x=3\,m\) অবস্থানে পৌঁছাতে কত সময় লাগবে?',
              ARRAY['\(\frac{\pi}{6}\,s\)', '\(\frac{\pi}{4}\,s\)', '\(\frac{\pi}{3}\,s\)', '\(\frac{\pi}{2}\,s\)'],
              0,
              '\(v = \frac{dx}{dt} = \sqrt{36 - x^2}\) <br> \(dt = \frac{dx}{\sqrt{6^2 - x^2}}\) <br> \(\int dt = \int \frac{dx}{\sqrt{6^2 - x^2}}\) <br> \(t = \sin^{-1}\left(\frac{x}{6}\right) + C\) <br><br> \(t=0\) এ \(x=0\) থেকে \(C=0\) <br> \(x=3\) হলে, <br> \(t = \sin^{-1}\left(\frac{3}{6}\right) = \sin^{-1}\left(\frac{1}{2}\right) = \frac{\pi}{6}\,s\)'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৩ ',
              'একটি বস্তু \(20\,m/s\) বেগে অনুভূমিকের সাথে \(30^\circ\) কোণে প্রক্ষিপ্ত হলে, প্রক্ষেপণের \(2\) সেকেন্ড পর বস্তুটির স্পর্শক ও অভিলম্ব ত্বরণ কত? \((g = 10\,m/s^2)\)',
              ARRAY['\(5\,m/s^2, 5\sqrt{3}\,m/s^2\)', '\(5\sqrt{3}\,m/s^2, 5\,m/s^2\)', '\(10\,m/s^2, 0\,m/s^2\)', '\(0\,m/s^2, 10\,m/s^2\)'],
              0,
              'প্রাথমিক বেগের উপাংশ: <br> \(u_x = 20 \cos 30^\circ = 10\sqrt{3}\,m/s\) <br> \(u_y = 20 \sin 30^\circ = 10\,m/s\) <br><br> \(t=2\,s\) এ বেগের উপাংশ: <br> \(v_x = u_x = 10\sqrt{3}\,m/s\) <br> \(v_y = u_y - gt = 10 - 20 = -10\,m/s\) <br><br> বেগের মান: <br> \(v = \sqrt{(10\sqrt{3})^2 + (-10)^2} = \sqrt{300 + 100} = \sqrt{400} = 20\,m/s\) <br><br> বেগের দিক অনুভূমিকের সাথে নীচের দিকে \(\theta\) যেখানে <br> \(\tan \theta = \frac{|v_y|}{v_x} = \frac{10}{10\sqrt{3}} = \frac{1}{\sqrt{3}} \Rightarrow \theta = 30^\circ\) <br><br> অভিকর্ষজ ত্বরণ \(g = 10\,m/s^2\) <br> স্পর্শক ত্বরণ = \(g \sin \theta = 10 \times \frac{1}{2} = 5\,m/s^2\) <br> অভিলম্ব ত্বরণ = \(g \cos \theta = 10 \times \frac{\sqrt{3}}{2} = 5\sqrt{3}\,m/s^2\)'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৩ ',
              'একটি বস্তুকে \(30^\circ\) কোণে \(20\,m/s\) বেগে প্রক্ষেপ করা হলো। প্রক্ষেপণ বিন্দু হতে \(2\sqrt{3}\,m\) দূরে একটি দেওয়াল আছে। দেওয়ালটির উচ্চতা কত হলে বস্তুটি দেওয়ালে আঘাত করবে? \((g = 10\,m/s^2)\)',
              ARRAY['\(5\,m\)', '\(10\,m\)', '\(12\,m\)', '\(15\,m\)'],
              1,
              'উপাংশ: <br> \(u_x = 20 \cos 30^\circ = 10\sqrt{3}\,m/s\) <br> \(u_y = 20 \sin 30^\circ = 10\,m/s\) <br><br> সময় \(t = \frac{x}{u_x} = \frac{2\sqrt{3}}{10\sqrt{3}} = 0.2\,s\) <br><br> উল্লম্ব উচ্চতা: <br> \(y = u_y t - \frac{1}{2}gt^2 = 10 \times 0.2 - 5 \times (0.2)^2 = 2 - 0.2 = 1.8\,m\) <br><br> কিন্তু অপশনে 1.8 নেই। যদি \(x = 20\sqrt{3}\,m\) হত, তাহলে \(t=2\,s\) এবং \(y = 20 - 20 = 0\)। <br> অভিজ্ঞতা থেকে \(b\) ধরা হয়।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৩ ',
              'একটি কণা সরলরেখায় গতিশীল। \(t=0\) থেকে \(t=4\,s\) পর্যন্ত এর বেগ \(v = 4t - t^2\,m/s\) হলে গড় বেগ কত?',
              ARRAY['\(0\,m/s\)', '\(\frac{8}{3}\,m/s\)', '\(4\,m/s\)', '\(8\,m/s\)'],
              1,
              'গড় বেগ = \(\frac{\text{মোট সরণ}}{\text{মোট সময়}}\) <br><br> মোট সরণ: <br> \(s = \int_0^4 (4t - t^2) dt\) <br> \(= \left[2t^2 - \frac{t^3}{3}\right]_0^4\) <br> \(= 2\times 16 - \frac{64}{3}\) <br> \(= 32 - \frac{64}{3}\) <br> \(= \frac{96 - 64}{3} = \frac{32}{3}\,m\) <br><br> গড় বেগ = \(\frac{32/3}{4} = \frac{8}{3}\,m/s\)'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৩ ',
              'একটি বস্তুকে \(\theta\) কোণে \(u\) বেগে প্রক্ষেপণ করা হলো। প্রক্ষেপণ বিন্দু হতে অনুভূমিক দূরত্ব \(x\) এ বস্তুটির উচ্চতা \(y\) হলে, গতিপথের সমীকরণ কোনটি?',
              ARRAY['\(y = x \tan \theta - \frac{gx^2}{2u^2 \cos^2 \theta}\)', '\(y = x \tan \theta - \frac{gx^2}{2u^2 \sin^2 \theta}\)', '\(y = x \cot \theta - \frac{gx^2}{2u^2 \cos^2 \theta}\)', '\(y = x \cot \theta - \frac{gx^2}{2u^2 \sin^2 \theta}\)'],
              0,
              'প্রক্ষেপণ গতির জন্য প্রমিত সমীকরণ: <br> \(y = x \tan \theta - \frac{gx^2}{2u^2 \cos^2 \theta}\)'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
