            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৪ ',
              'কোন অবস্থায় একটি লিফটের ভিতর থাকা ব্যক্তির আপাত ওজন প্রকৃত ওজনের চেয়ে কম হবে?',
              ARRAY['লিফট উপরের দিকে সমত্বরণে যাচ্ছে', 'লিফট নিচের দিকে সমত্বরণে যাচ্ছে', 'লিফট উপরের দিকে সমবেগে যাচ্ছে', 'লিফট স্থির অবস্থায় আছে'],
              1,
              'লিফট নিচের দিকে ত্বরণযুক্ত হলে আপাত ওজন m(g-a) হয়, যা প্রকৃত ওজন mg থেকে কম।
উপরের দিকে ত্বরণযুক্ত হলে আপাত ওজন বেড়ে m(g+a) হয়।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৪ ',
              'একটি বস্তুকে 30° আনত তলের উপর রাখা আছে। স্থিতি ঘর্ষণ গুণাঙ্ক 0.5 হলে বস্তুটি কি করবে? (g = 10 m/s²)',
              ARRAY['স্থির থাকবে', 'পিছলে নিচে নামবে', 'টলবে', 'উপরে উঠবে'],
              1,
              'আনত তলে নামার শর্ত, tanθ > μ। এখানে tan30° = 0.577 এবং μ = 0.5।
যেহেতু tanθ > μ, তাই বস্তুটি পিছলে নিচে নামবে।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
