INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'physics',
              'পদার্থ ১ম পত্র অধ্যায় ৪ ',
              '2 kg ভরের একটি বস্তু অনুভূমিক তলে 10 N বল প্রয়োগে গতিশীল। তল এবং বস্তুর মধ্যে গতীয় ঘর্ষণ গুণাঙ্ক 0.2 হলে বস্তুটির ত্বরণ কত? (g = 10 m/s²)',
              ARRAY['1 m/s²', '3 m/s²', '5 m/s²', '7 m/s²'],
