INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'chemistry',
              'রসায়ন ১ম পত্র অধ্যায় ২ ',
              'নিচের কোনটি আইসোটোনের উদাহরণ?',
              ARRAY['¹H¹ এবং ²H¹', '¹⁶O⁸ এবং ¹⁷O⁸', '¹⁴C⁶ এবং ¹⁴N⁷', '³He² এবং ⁴He²'],
              2,
              'আইসোটোন হলো সেই সকল পরমাণু যাদের নিউট্রন সংখ্যা সমান কিন্তু প্রোটন সংখ্যা ভিন্ন। ¹⁴C⁶ এর নিউট্রন সংখ্যা = ১৪-৬=৮ এবং ¹⁴N⁷ এর নিউট্রন সংখ্যা = ১৪-৭=৭? এই উদাহরণে নিউট্রন সংখ্যা সমান নয়। প্রকৃত আইসোটোনের উদাহরণ: ³⁶S¹⁶ (n=২০) এবং ³⁷Cl¹⁷ (n=২০) অথবা ¹⁴C⁶ (n=৮) এবং ¹⁵N⁷ (n=৮)।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'chemistry',
              'রসায়ন ১ম পত্র অধ্যায় ২ ',
              'বোরের মডেল অনুসারে, কক্ষপথের ব্যাসার্ধ কিসের সমানুপাতিক?',
              ARRAY['n²', 'n', '1/n', '1/n²'],
              0,
              'বোরের মডেল অনুসারে, n-তম কক্ষপথের ব্যাসার্ধ rₙ = n² × a₀, যেখানে a₀ একটি ধ্রুবক (বোর ব্যাসার্ধ)। সুতরাং, কক্ষপথের ব্যাসার্ধ n² এর সমানুপাতিক।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'chemistry',
              'রসায়ন ১ম পত্র অধ্যায় ২ ',
              'বোরের মডেল অনুসারে, কক্ষপথের শক্তি কিসের সমানুপাতিক?',
              ARRAY['n²', 'n', '1/n', '1/n²'],
              3,
              'বোরের মডেল অনুসারে, n-তম কক্ষপথের শক্তি Eₙ = -১৩.৬/n² eV। সুতরাং, কক্ষপথের শক্তি 1/n² এর সমানুপাতিক। ঋণাত্মক চিহ্ন নির্দেশ করে যে ইলেকট্রন নিউক্লিয়াসের সাথে আবদ্ধ।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'chemistry',
              'রসায়ন ১ম পত্র অধ্যায় ২ ',
              'হাইড্রোজেন বর্ণালীতে বামার সিরিজের প্রথম রেখার তরঙ্গদৈর্ঘ্য কত? (R = ১.০৯৭ × ১০⁷ m⁻¹)',
              ARRAY['৬৫৬.৩ nm', '৪৮৬.১ nm', '৪৩৪.১ nm', '৪১০.২ nm'],
              0,
              'বামার সিরিজের জন্য ১/λ = R(১/২² - ১/n²)। প্রথম রেখার জন্য n=3। সুতরাং ১/λ = ১.০৯৭×১০⁷(১/৪ - ১/৯) = ১.০৯৭×১০⁷(৫/৩৬) = ১.০৯৭×১০⁷×০.১৩৮৯ = ১.৫২৪×১০⁶ m⁻¹। সুতরাং λ = ১/১.৫২৪×১০⁶ = ৬.৫৬৩×১০⁻⁷ m = ৬৫৬.৩ nm।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'chemistry',
              'রসায়ন ১ম পত্র অধ্যায় ২ ',
              'পারমাণবিক সংখ্যা সমান কিন্তু ভর সংখ্যা ভিন্ন হলে তাদের কী বলে?',
              ARRAY['আইসোটোপ', 'আইসোবার', 'আইসোটোন', 'সমতুল্য'],
              0,
              'পারমাণবিক সংখ্যা সমান কিন্তু ভর সংখ্যা ভিন্ন হলে তাদের আইসোটোপ বলে। যেমন: ³⁵Cl¹⁷ এবং ³⁷Cl¹⁷ - উভয়ের পারমাণবিক সংখ্যা ১৭ কিন্তু ভর সংখ্যা ৩৫ ও ৩৭।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
