              'সোডিয়ামের কোনো প্রাকৃতিক আইসোটোপ নেই। হাইড্রোজেনের তিনটি আইসোটোপ আছে (প্রোটিয়াম, ডিউটেরিয়াম, ট্রিটিয়াম), হিলিয়ামের দুটি (He-3, He-4) এবং ক্লোরিনের দুটি (Cl-35, Cl-37)।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'chemistry',
              'রসায়ন ১ম পত্র অধ্যায় ২ ',
