INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
              'hsc',
              'chemistry',
              'রসায়ন ১ম পত্র অধ্যায় ২ ',
              'প্লাঙ্কের ধ্রুবকের মাত্রা কোনটি?',
              ARRAY['শক্তি × সময়', 'শক্তি / সময়', 'দৈর্ঘ্য / সময়', 'ভর × দৈর্ঘ্য'],
              0,
              'প্লাঙ্কের ধ্রুবক h = E/ν, যেখানে E = শক্তি এবং ν = কম্পাঙ্ক। কম্পাঙ্কের মাত্রা হলো T⁻¹, তাই h এর মাত্রা = (ML²T⁻²)/T⁻¹ = ML²T⁻¹, যা শক্তি × সময় এর সমান। এর একক J.s।'
            );
INSERT INTO questions (category_group, subject_id, chapter_name, question_text, options, correct_option, explanation) VALUES (
